package com.todoList.demo.controller;import com.todoList.demo.entity.Note;import com.todoList.demo.service.NoteService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.web.bind.annotation.PostMapping;import org.springframework.web.bind.annotation.RequestBody;import org.springframework.web.bind.annotation.RestController;@RestControllerpublic class NoteConttoller {    @Autowired    NoteService noteService;    @PostMapping("/note")    public Note createNote(@RequestBody Note note) {        return noteService.createNote(note);    }}