package com.todoList.demo.controller;import com.todoList.demo.ResponseHandler;import com.todoList.demo.entity.Note;import com.todoList.demo.service.NoteService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.http.HttpStatus;import org.springframework.http.ResponseEntity;import org.springframework.web.bind.annotation.*;import javax.validation.Valid;import java.util.List;@RestController@RequestMapping("/user")public class NoteConttoller {    @Autowired    NoteService noteService;    @PostMapping("/note")    public ResponseEntity<Object> createNote(@Valid @RequestBody Note note) {        return ResponseHandler.responseBuilder("Note Added", HttpStatus.OK,                noteService.createNote(note));    }    @GetMapping("/recent")    public ResponseEntity<Object>  getRecentNotes() {        return ResponseHandler.responseBuilder("Fetched",HttpStatus.OK, noteService.getMostRecentNotes());    }    @DeleteMapping("/delete/{id}")    public ResponseEntity<Object>  delete(@PathVariable Long id) {         return ResponseHandler.responseBuilder("Deleted",HttpStatus.OK,noteService.delete(id));    }    @DeleteMapping("/delete")    public ResponseEntity<Object>  delete() {        return ResponseHandler.responseBuilder("Deleted",HttpStatus.OK,noteService.deleteRecords());    }}