package com.todoList.demo.controller;import com.todoList.demo.ResponseHandler;import com.todoList.demo.entity.Login;import com.todoList.demo.entity.Users;import com.todoList.demo.service.UsersService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.http.HttpStatus;import org.springframework.http.ResponseEntity;import org.springframework.web.bind.annotation.PostMapping;import org.springframework.web.bind.annotation.RequestBody;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RestController;import javax.validation.Valid;@RestController@RequestMapping("/user")public class UsersController {    @Autowired    UsersService usersService;    @PostMapping("/register")    public ResponseEntity<Object>register(@Valid @RequestBody Users users){        return ResponseHandler.responseBuilder("User registered ", HttpStatus.OK,usersService.register(users));    }    @PostMapping("/login")    public ResponseEntity<Object>login(@Valid @RequestBody Login login){        return ResponseHandler.responseBuilder("logged in",HttpStatus.OK,usersService.login(login));    }}