package com.todoList.demo.entity;import lombok.*;import javax.persistence.Entity;import javax.persistence.GeneratedValue;import javax.persistence.GenerationType;import javax.persistence.Id;import javax.validation.constraints.Email;import javax.validation.constraints.NotEmpty;@Entity@Getter@Setter@NoArgsConstructor@AllArgsConstructorpublic class Users {    @Id    @GeneratedValue(strategy = GenerationType.IDENTITY)    private Integer userId;    @NotEmpty(message = "Required field")    private String firstName;    @NotEmpty(message = "Required field")    private String lastName;    @NotEmpty    @Email    private String email;    @NotEmpty(message = "Required field")    private String pass;}