package com.todoList.demo.exception;import lombok.AllArgsConstructor;import lombok.Data;import lombok.NoArgsConstructor;public class Response {    private String response;    private int status_code;    public String getResponse() {        return response;    }    public void setResponse(String response) {        this.response = response;    }    public int getStatus_code() {        return status_code;    }    public void setStatus_code(int status_code) {        this.status_code = status_code;    }    public Response() {    }    public Response(String response, int status_code) {        this.response = response;        this.status_code = status_code;    }}