package com.todoList.demo.security;import com.todoList.demo.exception.CustomException;import com.todoList.demo.repository.UserRepository;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.security.core.userdetails.UserDetails;import org.springframework.security.core.userdetails.UserDetailsService;import org.springframework.security.core.userdetails.UsernameNotFoundException;import org.springframework.stereotype.Service;@Servicepublic class CustomUserDetailService implements UserDetailsService {    @Autowired    UserRepository employeeRepository;    @Override    public UserDetails loadUserByUsername(String username) throws UsernameNotFoundException {        return (UserDetails) this.employeeRepository.findByUserName(username)                .orElseThrow(() -> new CustomException("employee not found"));    }}