package com.todoList.demo.service;import com.todoList.demo.entity.Employee;import com.todoList.demo.repository.UserRepository;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.security.crypto.bcrypt.BCryptPasswordEncoder;import org.springframework.security.crypto.password.PasswordEncoder;import org.springframework.stereotype.Service;@Servicepublic class UsersService {    @Autowired    UserRepository userRepository;    @Autowired    PasswordEncoder passwordEncoder;    public Employee register(Employee users){        String encode= passwordEncoder.encode(users.getPassword());        users.setPassword(encode);        return userRepository.save(users);    }    public Employee user(String userName){        return userRepository.getByUserName(userName);    }}