package com.todoList.demo.service;import com.todoList.demo.entity.Login;import com.todoList.demo.entity.Users;import com.todoList.demo.exception.CustomException;import com.todoList.demo.repository.UserRepository;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import java.util.Objects;@Servicepublic class UsersService {    @Autowired    UserRepository userRepository;    public Users register(Users users){        return userRepository.save(users);    }    public Object login(Login login){        Users users=userRepository.findByEmail(login.email);        if (users!=null){            if (Objects.equals(users.getPass(), login.pass)){                return null;            }else throw new CustomException("Password do not match");        }else throw new CustomException("User name not found");    }}